import React, { useState } from 'react';
import InputField from '../components/InputField';
import Button from '../components/Button';

const BookingDashboard = () => {
  // State variables for form fields
  const [tripId, setTripId] = useState('');
  const [customerId, setCustomerId] = useState('');
  const [source, setSource] = useState('');
  const [destination, setDestination] = useState('');
  const [journeyDate, setJourneyDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [customerName, setCustomerName] = useState('');
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');

  // Inline styles
  const containerStyle = {
    padding: '20px',
    backgroundColor: '#2b2b42',
    color: '#fff',
    minHeight: '100vh',
    textAlign: 'center',
  };

  const formStyle = {
    maxWidth: '600px',
    margin: '0 auto',
    backgroundColor: '#3e3e58',
    padding: '20px',
    borderRadius: '8px',
  };

  const buttonContainerStyle = {
    display: 'flex',
    justifyContent: 'space-around',
    marginTop: '20px',
  };

  return (
    <div style={containerStyle}>
      <h2 style={{ marginBottom: '20px' }}>Booking Dashboard</h2>
      <div style={formStyle}>
        <InputField
          label="Trip ID"
          value={tripId}
          onChange={(e) => setTripId(e.target.value)}
        />
        <InputField
          label="Customer ID"
          value={customerId}
          onChange={(e) => setCustomerId(e.target.value)}
        />
        <InputField
          label="Source"
          value={source}
          onChange={(e) => setSource(e.target.value)}
        />
        <InputField
          label="Destination"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
        <InputField
          label="Journey Date"
          type="date"
          value={journeyDate}
          onChange={(e) => setJourneyDate(e.target.value)}
        />
        <InputField
          label="End Date"
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />
        <InputField
          label="Customer Name"
          value={customerName}
          onChange={(e) => setCustomerName(e.target.value)}
        />
        <InputField
          label="Address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
        <InputField
          label="Phone Number"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />

        <div style={buttonContainerStyle}>
          <Button text="Book" color="#28a745" onClick={() => alert('Trip Booked!')} />
          <Button text="Modify" color="#17a2b8" onClick={() => alert('Trip Modified!')} />
          <Button text="Delete" color="#dc3545" onClick={() => alert('Trip Deleted!')} />
          <Button text="Clear" color="#ffc107" onClick={() => {
            setTripId('');
            setCustomerId('');
            setSource('');
            setDestination('');
            setJourneyDate('');
            setEndDate('');
            setCustomerName('');
            setAddress('');
            setPhone('');
          }} />
        </div>
      </div>
    </div>
  );
};

export default BookingDashboard;
