// import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const ConfirmationPage = () => {
  const { state } = useLocation();

  if (!state) {
    return (
      <div
        style={{
          minHeight: '100vh',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          fontFamily: 'Arial, sans-serif',
          background: 'linear-gradient(135deg, #4caf50, #81c784)',
          color: '#fff',
          padding: '20px',
        }}
      >
        <p>No booking details available.</p>
      </div>
    );
  }

  const { customerId } = state;  // Assuming customerId is included in the state
  const [paymentStatus, setPaymentStatus] = useState(null);
  const [amount, setAmount] = useState(100);  // Set a default amount or dynamically calculate it

  const handlePayment = async () => {
    try {
      // Call the backend API to store the payment details
      const response = await fetch('http://localhost:5000/api/payment', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          customerId,
          amount,
        }),
      });

      const data = await response.json();
      if (data.success) {
        setPaymentStatus('Payment successful!');
      } else {
        setPaymentStatus('Payment failed, please try again.');
      }
    } catch (error) {
      console.error('Error processing payment:', error);
      setPaymentStatus('Payment failed, please try again.');
    }
  };

  return (
    <div
      style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundImage: 'url("https://source.unsplash.com/1600x900/?success,travel")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        fontFamily: 'Arial, sans-serif',
        padding: '20px',
      }}
    >
      <div
        style={{
          background: 'rgba(255, 255, 255, 0.9)',
          padding: '30px',
          borderRadius: '10px',
          boxShadow: '0 8px 15px rgba(0, 0, 0, 0.2)',
          maxWidth: '600px',
          width: '100%',
          textAlign: 'center',
        }}
      >
        <h2 style={{ color: '#4caf50', marginBottom: '20px' }}>Booking Successful!</h2>
        <p style={{ fontSize: '18px', marginBottom: '10px' }}>
          Thank you for your booking!
        </p>

        {/* Payment Button */}
        <button
          style={{
            marginTop: '20px',
            padding: '10px 20px',
            background: 'linear-gradient(90deg, #4caf50, #81c784)',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '16px',
            transition: 'all 0.3s ease',
          }}
          onClick={handlePayment}
        >
          Pay Now
        </button>

        {/* Display Payment Status */}
        {paymentStatus && (
          <p style={{ marginTop: '20px', fontWeight: 'bold', color: paymentStatus === 'Payment successful!' ? 'green' : 'red' }}>
            {paymentStatus}
          </p>
        )}

        <button
          style={{
            marginTop: '20px',
            padding: '10px 20px',
            background: 'linear-gradient(90deg, #4caf50, #81c784)',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '16px',
            transition: 'all 0.3s ease',
          }}
          onClick={() => window.location.href = '/'}
        >
          Go Back to Home
        </button>
      </div>
    </div>
  );
};

export default ConfirmationPage;
