import React from 'react';
import { useNavigate } from 'react-router-dom';
import Button from '../components/Button';

const HomePage = () => {
  const navigate = useNavigate();
  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: '#343a40',
    color: '#fff',
    textAlign: 'center',
    flexDirection: 'column',
  };

  const buttonStyle = {
    backgroundColor: 'blue', // Blue button background
    color: 'black', // Black text color
    border: 'none', // Remove border
    padding: '10px 20px', // Add padding
    fontSize: '16px', // Set font size
    cursor: 'pointer', // Change cursor to pointer on hover
    margin: '10px 0', // Margin between buttons
  };

  return (
    <div style={containerStyle}>
      <h1>Welcome to the Travel Agency System</h1>
      <p>Manage vehicles, employees, trips, and bookings efficiently.</p>
      <Button 
        text="Go to Employee Dashboard" 
        onClick={() => navigate('/employee')} 
        style={buttonStyle} 
      />
      <Button 
        text="Go to Booking Dashboard" 
        onClick={() => navigate('/booking')} 
        style={buttonStyle} 
      />
    </div>
  );
};

export default HomePage;
