import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LoginPage from './pages/LoginPage';  // Import LoginPage
import HomePage from './pages/HomePage';
import EmployeeDashboard from './pages/EmployeeDashboard';
import BookingDashboard from './pages/BookingDashboard';
import ConfirmBooking from './pages/ConfirmBooking';
import ConfirmationPage from './pages/ConfirmationPage';

const App = () => {
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    // Replace this with API integration to fetch user details or authentication status.
    const fetchUserRole = async () => {
      try {
        const response = await fetch('/api/auth/user-role'); // Example API endpoint
        const data = await response.json();
        setUserRole(data.role); // 'admin' or 'customer'
      } catch (error) {
        console.error('Error fetching user role:', error);
        setUserRole(null); // Handle unauthenticated state
      }
    };

    fetchUserRole();
  }, []);

  if (userRole === null) {
    // Optionally, show a loading spinner while user role is being determined
    return <div>Loading...</div>;
  }
  return (
    <Router>
      <Routes>
 <Route path="/" element={<LoginPage />} />
        {userRole === 'admin' && <Route path="/admin-dashboard" element={<AdminDashboard />} />}
        <Route path="/customer-dashboard" element={<CustomerDashboard />} />
        <Route path="/login" element={<LoginPage />} />
        {/* Redirect unknown routes */}
        <Route path="*" element={<Navigate to="/" />} />
      <Route path="/employee" element={<EmployeeDashboard />} />
      <Route path="/booking" element={<BookingDashboard />} />
      <Route path="/confirm-booking" element={<ConfirmBooking />} />
      <Route path="/confirmation" element={<ConfirmationPage />} />
      </Routes>
    </Router>
  );
};

export default App;
