import React, { useState } from 'react';
import axios from 'axios';
import InputField from '../components/InputField';
import Button from '../components/Button';

const EmployeeDashboard = () => {
  const [employeeId, setEmployeeId] = useState('');
  const [employeeName, setEmployeeName] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');
  const [joiningDate, setJoiningDate] = useState('');

  const containerStyle = {
    padding: '20px',
    backgroundColor: '#2b2b42',
    color: '#fff',
    minHeight: '100vh',
    textAlign: 'center',
  };

  const formStyle = {
    maxWidth: '500px',
    margin: '0 auto',
    backgroundColor: '#3e3e58',
    padding: '20px',
    borderRadius: '8px',
  };

  // Function to handle Add button click
  const handleAdd = async () => {
    try {
      console.log('Sending data to backend:', {
        employeeName,
        phone,
        address,
        joiningDate,
      }); // Log the data being sent
  
      const response = await axios.post('http://localhost:5000/add-employee', {
        employeeName,
        phone,
        address,
        joiningDate,
      });
  
      if (response.status === 200) {
        alert(response.data.message);
        clearFields(); // Clear the form fields on success
      } else {
        alert('Failed to add employee: ' + response.data.error);
      }
    } catch (error) {
      console.error('Error adding employee:', error);
      alert('Error adding employee. Please try again.');
    }
  };
  

  // Function to clear fields
  const clearFields = () => {
    setEmployeeId('');
    setEmployeeName('');
    setPhone('');
    setAddress('');
    setJoiningDate('');
  };

  return (
    <div style={containerStyle}>
      <h2>Employee Dashboard</h2>
      <div style={formStyle}>
        <InputField label="Employee ID" value={employeeId} onChange={(e) => setEmployeeId(e.target.value)} />
        <InputField label="Employee Name" value={employeeName} onChange={(e) => setEmployeeName(e.target.value)} />
        <InputField label="Phone Number" value={phone} onChange={(e) => setPhone(e.target.value)} />
        <InputField label="Address" value={address} onChange={(e) => setAddress(e.target.value)} />
        <InputField
          label="Joining Date"
          type="date"
          value={joiningDate}
          onChange={(e) => setJoiningDate(e.target.value)}
        />
        <Button text="Add" color="#28a745" onClick={handleAdd} />
        <Button text="Modify" color="#17a2b8" />
        <Button text="Clear" color="#ffc107" onClick={clearFields} />
        <Button text="Delete" color="#dc3545" />
      </div>
    </div>
  );
};

export default EmployeeDashboard;
