import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const ConfirmBooking = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  const handleConfirm = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/booking', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(state),
      });

      if (response.ok) {
        const data = await response.json();
        navigate('/confirmation', { state: data });
      } else {
        console.error('Error:', await response.text());
      }
    } catch (error) {
      console.error('Network Error:', error);
    }
  };

  return (
    <div
      style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundImage: 'url("https://source.unsplash.com/1600x900/?travel,booking")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        fontFamily: 'Arial, sans-serif',
        padding: '20px',
      }}
    >
      <div
        style={{
          background: 'rgba(255, 255, 255, 0.9)',
          padding: '30px',
          borderRadius: '10px',
          boxShadow: '0 8px 15px rgba(0, 0, 0, 0.2)',
          maxWidth: '500px',
          width: '100%',
          textAlign: 'center',
        }}
      >
        <h2 style={{ marginBottom: '20px', color: '#333' }}>Confirm Booking</h2>
        <div
          style={{
            padding: '20px',
            background: '#f9f9f9',
            borderRadius: '5px',
            marginBottom: '20px',
            textAlign: 'left',
          }}
        >
          <p>
            <strong>Customer Name:</strong> {state.customerName}
          </p>
          <p>
            <strong>Address:</strong> {state.address}
          </p>
          <p>
            <strong>Phone:</strong> {state.phone}
          </p>
          <p>
            <strong>Source:</strong> {state.source}
          </p>
          <p>
            <strong>Destination:</strong> {state.destination}
          </p>
          <p>
            <strong>Journey Date:</strong> {state.journeyDate}
          </p>
          <p>
            <strong>End Date:</strong> {state.endDate}
          </p>
          <p>
            <strong>Total Amount</strong>
          </p>
        </div>
        <button
          onClick={handleConfirm}
          style={{
            padding: '10px 20px',
            background: 'linear-gradient(90deg, #4caf50, #81c784)',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '16px',
            transition: 'all 0.3s ease',
          }}
          onMouseOver={(e) => {
            e.target.style.transform = 'scale(1.1)';
            e.target.style.boxShadow = '0 6px 8px rgba(0, 0, 0, 0.3)';
          }}
          onMouseOut={(e) => {
            e.target.style.transform = 'scale(1)';
            e.target.style.boxShadow = 'none';
          }}
        >
          Confirm
        </button>
      </div>
    </div>
  );
};

export default ConfirmBooking;
