import React, { useState, useEffect } from 'react';
import axios from 'axios';
import InputField from '../components/InputField';
import Button from '../components/Button';

const EmployeeDashboard = () => {
  const [employeeId, setEmployeeId] = useState('');
  const [employeeName, setEmployeeName] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');
  const [joiningDate, setJoiningDate] = useState('');
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:5000/employees');
      setEmployees(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const handleAdd = async () => {
    try {
      const response = await axios.post('http://localhost:5000/add-employee', {
        employeeName,
        phone,
        address,
        joiningDate,
      });
      alert(response.data.message);
      fetchEmployees();
      clearFields();
    } catch (error) {
      console.error('Error adding employee:', error);
    }
  };

  const handleDelete = async () => {
    try {
      const response = await axios.delete(`http://localhost:5000/delete-employee/${employeeId}`);
      alert(response.data.message);
      fetchEmployees();
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  const handleModify = async () => {
    try {
      const response = await axios.put(`http://localhost:5000/modify-employee/${employeeId}`, {
        employeeName,
        phone,
        address,
        joiningDate,
      });
      alert(response.data.message);
      fetchEmployees();
    } catch (error) {
      console.error('Error modifying employee:', error);
    }
  };

  const clearFields = () => {
    setEmployeeId('');
    setEmployeeName('');
    setPhone('');
    setAddress('');
    setJoiningDate('');
  };

  return (
    <div style={{ padding: '20px',  background: 'linear-gradient(90deg,rgb(20, 15, 65),rgb(79, 97, 59))', color: '#fff', minHeight: '100vh' }}>
      <h2>Employee Dashboard</h2>
      <div style={{ maxWidth: '500px', margin: '0 auto', backgroundColor: '#3e3e58', padding: '20px', borderRadius: '8px' }}>
        <InputField label="Employee ID" value={employeeId} onChange={(e) => setEmployeeId(e.target.value)} />
        <InputField label="Employee Name" value={employeeName} onChange={(e) => setEmployeeName(e.target.value)} />
        <InputField label="Phone" value={phone} onChange={(e) => setPhone(e.target.value)} />
        <InputField label="Address" value={address} onChange={(e) => setAddress(e.target.value)} />
        <InputField label="Joining Date" type="date" value={joiningDate} onChange={(e) => setJoiningDate(e.target.value)} />
        <Button text="Add" color="#28a745" onClick={handleAdd} />
        <Button text="Modify" color="#17a2b8" onClick={handleModify} />
        <Button text="Delete" color="#dc3545" onClick={handleDelete} />
        <Button text="Clear" color="#ffc107" onClick={clearFields} />
      </div>
      <h3>Employee List</h3>
      <ul>
        {employees.map((emp) => (
          <li key={emp.id}>
            {emp.id} - {emp.name} - {emp.phone}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeDashboard;
