import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const ConfirmBooking = () => {
    const { state } = useLocation();
    const navigate = useNavigate();
    const [guide, setGuide] = useState(null);

    useEffect(() => {
        const fetchGuide = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/available-guide?packageId=${state.packageId}`);
                if (response.ok) {
                    const guideData = await response.json();
                    setGuide(guideData);
                } else {
                    console.error('Error fetching guide:', await response.text());
                }
            } catch (error) {
                console.error('Network error fetching guide:', error);
            }
        };

        fetchGuide();
    }, [state.packageId]);

    const handleConfirm = async () => {
        try {
            const bookingDetails = { ...state, guideName: guide.name };
            const response = await fetch('http://localhost:5000/api/booking', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(bookingDetails),
            });

            if (response.ok) {
                const data = await response.json();
                navigate('/confirmation', { state: data });
            } else {
                console.error('Error confirming booking:', await response.text());
            }
        } catch (error) {
            console.error('Network error confirming booking:', error);
        }
    };

    return (
        <div>
            <h2>Confirm Booking</h2>
            <p>Customer Name: {state.customerName}</p>
            <p>Journey Date: {state.journeyDate}</p>
            <p>End Date: {state.endDate}</p>
            <p>Price: ${state.price}</p>
            {guide && <p>Guide Name: {guide.name}</p>}
            <button onClick={handleConfirm}>Confirm</button>
        </div>
    );
};

export default ConfirmBooking;
