import React from 'react';

const HomePage = () => {
  // Inline styles for HomePage
  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundColor: '#007bff',
    color: '#fff',
    textAlign: 'center',
    flexDirection: 'column',
  };

  const headingStyle = {
    fontSize: '40px',
    fontWeight: 'bold',
    marginBottom: '20px',
  };

  const paragraphStyle = {
    fontSize: '20px',
    maxWidth: '500px',
    lineHeight: '1.5',
    marginBottom: '40px',
  };

  const buttonStyle = {
    padding: '12px 30px',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    fontSize: '18px',
    cursor: 'pointer',
  };

  return (
    <div style={containerStyle}>
      <h1 style={headingStyle}>Welcome to the Travel Agency System</h1>
      <p style={paragraphStyle}>
        Manage your vehicles, employees, trips, customers, and bookings with ease. Access all your data in one place!
      </p>
      <button style={buttonStyle} onClick={() => alert('Manage your data')}>
        Get Started
      </button>
    </div>
  );
};

export default HomePage;
