import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import InputField from '../components/InputField';
import Button from '../components/Button';

const ConfirmBooking = () => {
  const { state } = useLocation(); // Get data from BookingDashboard
  const navigate = useNavigate();

  const handleConfirmBooking = () => {
    fetch('/api/bookings', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(state),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message) {
          alert('Booking successful!');
          navigate('/'); // Redirect to home or another page
        } else {
          alert('Error: ' + data.error);
        }
      })
      .catch((error) => {
        console.error('Error:', error);
        alert('Failed to confirm booking.');
      });
  };

  return (
    <div style={{ padding: '20px', backgroundColor: '#2b2b42', color: '#fff', minHeight: '100vh', textAlign: 'center' }}>
      <h2>Confirm Booking</h2>
      <div style={{ maxWidth: '600px', margin: '0 auto', backgroundColor: '#3e3e58', padding: '20px', borderRadius: '8px' }}>
        <InputField label="Customer ID" value={state.customerId} readOnly />
        <InputField label="Trip ID" value={state.tripId} readOnly />
        <InputField label="Source" value={state.source} readOnly />
        <InputField label="Destination" value={state.destination} readOnly />
        <InputField label="Journey Date" value={state.journeyDate} readOnly />
        <InputField label="End Date" value={state.endDate} readOnly />
        <InputField label="Customer Name" value={state.customerName} readOnly />
        <InputField label="Address" value={state.address} readOnly />
        <InputField label="Phone Number" value={state.phone} readOnly />

        <div style={{ marginTop: '20px' }}>
          <Button text="Confirm Booking" color="#28a745" onClick={handleConfirmBooking} />
        </div>
      </div>
    </div>
  );
};

export default ConfirmBooking;
